// sidebar.min.js
// 动态生成悬浮左侧边栏，内容从 data.json main-menu 读取，默认隐藏，仅显示状态球，点击展开/收起

(function() {
    // 创建状态球按钮
    const toggleBtn = document.createElement('button');
    toggleBtn.id = 'sidebar-toggle-btn';
    toggleBtn.type = 'button';
    toggleBtn.title = '展开菜单';
    toggleBtn.setAttribute('aria-label', '展开侧边栏');
    toggleBtn.innerHTML = `
        <span class="visually-hidden">展开侧边栏</span>
        <span class="sidebar-toggle__glow" aria-hidden="true"></span>
        <svg class="sidebar-toggle__icon" viewBox="0 0 24 24" role="img" aria-hidden="true">
            <path d="M6 7h12v2H6V7zm0 4h12v2H6v-2zm0 4h12v2H6v-2z"></path>
        </svg>
    `;
    document.body.appendChild(toggleBtn);
    toggleBtn.style.left = '0';

    // 创建侧边栏容器
    const sidebar = document.createElement('nav');
    sidebar.id = 'custom-sidebar';
    sidebar.innerHTML = '<ul id="sidebar-menu"></ul>';
    document.body.appendChild(sidebar);

    // 样式注入
    const style = document.createElement('style');
    style.textContent = `
    #sidebar-toggle-btn {
        position: fixed;
        top: 50%;
        left: 0;
        z-index: 10000;
        width: 40px;
        height: 92px;
        padding: 0;
        border: none;
        border-top-right-radius: 38px;
        border-bottom-right-radius: 38px;
        border-top-left-radius: 0;
        border-bottom-left-radius: 0;
        background: linear-gradient(180deg, #3b4e60, #1c2732);
        box-shadow: inset 0 2px 3px rgba(255,255,255,0.08), inset 0 -3px 4px rgba(0,0,0,0.55), 0 10px 26px rgba(44,62,80,0.4);
        transform: translateY(-50%);
        display: flex;
        align-items: center;
        justify-content: flex-end;
        cursor: pointer;
        transition: left 0.3s ease, transform 0.25s ease, box-shadow 0.3s ease;
        overflow: hidden;
    }
    #sidebar-toggle-btn::after {
        content: "";
        position: absolute;
        inset: 3px 6px 3px 0;
        border-top-right-radius: 32px;
        border-bottom-right-radius: 32px;
        border-top-left-radius: 0;
        border-bottom-left-radius: 0;
        border: 1.5px solid rgba(255, 255, 255, 0.12);
        border-left: none;
        pointer-events: none;
    }
    #sidebar-toggle-btn:hover {
        transform: translateY(-52%);
        box-shadow: inset 0 2px 3px rgba(255,255,255,0.15), inset 0 -3px 4px rgba(0,0,0,0.65), 0 18px 32px rgba(28,39,50,0.45);
    }
    #sidebar-toggle-btn:focus-visible {
        outline: 2px solid rgba(255, 255, 255, 0.8);
        outline-offset: 4px;
    }
    #sidebar-toggle-btn .sidebar-toggle__glow {
        position: absolute;
        left: 6px;
        top: 16px;
        width: 10px;
        height: 60px;
        border-radius: 20px;
        background: linear-gradient(180deg, rgba(255,255,255,0.2), rgba(255,255,255,0));
        filter: blur(0.5px);
        opacity: 0.8;
        transition: opacity 0.3s ease, transform 0.3s ease;
    }
    #sidebar-toggle-btn:hover .sidebar-toggle__glow {
        opacity: 1;
        transform: translateX(1px);
    }
    #sidebar-toggle-btn .sidebar-toggle__icon {
        width: 24px;
        height: 24px;
        margin-right: 8px;
        fill: #e6eaef;
        filter: drop-shadow(0 2px 4px rgba(0,0,0,0.6));
        transition: fill 0.3s ease;
    }
    #sidebar-toggle-btn:hover .sidebar-toggle__icon {
        fill: #ffffff;
    }
    #custom-sidebar {
        position: fixed;
        top: 50%;
        left: -220px;
        z-index: 9999;
        background: var(--bg-white);
        box-shadow: var(--shadow-md);
        border-radius: 22px;
        padding: 18px 0 18px 0;
        min-width: 72px;
        max-width: 160px;
        width: auto;
        display: flex;
        flex-direction: column;
        align-items: center;
        border: 1px solid var(--border-light);
        transition: left 0.35s cubic-bezier(.77,0,.18,1), box-shadow 0.3s, border 0.3s;
        transform: translateY(-50%);
        background-clip: padding-box;
    }
    #custom-sidebar.sidebar-open {
        left: 48px;
        box-shadow: var(--shadow-primary);
        border-color: var(--border-dark);
    }
    #custom-sidebar ul {
        list-style: none;
        margin: 0;
        padding: 0;
        width: 100%;
        display: flex;
        flex-direction: column;
        align-items: center;
    }
    #custom-sidebar li {
        width: 100%;
        margin: 0 0 8px 0;
        display: flex;
        flex-direction: column;
        align-items: center;
    }
    #custom-sidebar li:last-child { margin-bottom: 0; }
    #custom-sidebar a {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        width: calc(100% - 12px);
        min-width: 72px;
        max-width: 160px;
        padding: 12px 0 8px 0;
        margin: 0 6px 0 6px;
        border-radius: 16px;
        color: var(--text-primary);
        font-size: 1.15rem;
        font-weight: 500;
        background: none;
        border: none;
        transition: background 0.3s, color 0.3s, box-shadow 0.3s;
        cursor: pointer;
        text-decoration: none;
        position: relative;
        gap: 4px;
        box-sizing: border-box;
    }
    #custom-sidebar a i {
        font-size: 2.1rem;
        margin-bottom: 2px;
        transition: color 0.3s, transform 0.3s;
    }
    #custom-sidebar a:hover {
        background: var(--primary-gradient);
        color: #fff;
        box-shadow: var(--shadow-primary);
    }
    #custom-sidebar a:hover i {
        color: #fff;
        transform: scale(1.12);
    }
    #custom-sidebar a:active {
        transform: scale(0.97);
    }
    #custom-sidebar a span {
        margin-top: 2px;
        font-size: 1.50rem;
        font-weight: 400;
        letter-spacing: 0.5px;
        transition: color 0.3s;
    }
    @media (max-width: 700px) {
        #sidebar-toggle-btn {
            left: 0;
            width: 32px;
            height: 32px;
        }
        #custom-sidebar {
            left: -140px;
            min-width: 56px;
            max-width: 100px;
        }
        #custom-sidebar.sidebar-open {
            left: 36px;
        }
        #custom-sidebar a span {
            font-size: 1.1rem;
        }
        #custom-sidebar a i {
            font-size: 1.2rem;
        }
    }
    body.dark-theme #custom-sidebar {
        background: rgba(24, 28, 34, 0.92);
        border-color: rgba(255, 255, 255, 0.05);
        box-shadow: var(--shadow-lg);
    }
    body.dark-theme #custom-sidebar a {
        color: var(--text-light);
        background: none;
    }
    body.dark-theme #custom-sidebar a span,
    body.dark-theme #custom-sidebar a i {
        color: inherit;
    }
    body.dark-theme #sidebar-toggle-btn {
        background: linear-gradient(180deg, #243545, #121a22);
        box-shadow: inset 0 2px 3px rgba(255,255,255,0.04), inset 0 -3px 4px rgba(0,0,0,0.65), 0 10px 24px rgba(0,0,0,0.55);
    }
    body.dark-theme #sidebar-toggle-btn::after {
        border-color: rgba(255, 255, 255, 0.08);
    }
    /* 选中按钮高亮与主色一致 */
    #custom-sidebar a.active,
    #custom-sidebar a:focus {
        background: var(--primary-gradient) !important;
        color: #fff !important;
        box-shadow: var(--shadow-primary);
    }
    #custom-sidebar a.active i,
    #custom-sidebar a:focus i {
        color: #fff !important;
    }
    /* 夜间模式选中按钮高亮 */
    body.dark-theme #custom-sidebar a.active,
    body.dark-theme #custom-sidebar a:focus {
        background: var(--primary-gradient) !important;
        color: #fff !important;
        box-shadow: var(--shadow-primary) !important;
    }
    body.dark-theme #custom-sidebar a.active i,
    body.dark-theme #custom-sidebar a:focus i {
        color: #fff !important;
    }
    `;
    document.head.appendChild(style);

    // 读取 data.json main-menu
    fetch('data.json')
        .then(res => res.json())
        .then(data => {
            const menu = data['main-menu'];
            const ul = document.getElementById('sidebar-menu');
            Object.values(menu).forEach(item => {
                const li = document.createElement('li');
                li.innerHTML = `<a href="${item.href}"><i class="${item.icon}"></i><span>${item.title}</span></a>`;
                ul.appendChild(li);
            });
        });

    // 侧边栏切换功能
    let sidebarVisible = false;
    let isDragging = false;
    let startY = 0;
    let startTop = 0;

    function toggleSidebar() {
        if (isDragging) return; // 如果正在拖拽，不切换侧边栏
        
        sidebarVisible = !sidebarVisible;
        const sidebar = document.getElementById('custom-sidebar');
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        const sidebarWidth = sidebar.offsetWidth;
        
        if (sidebarVisible) {
            sidebar.style.left = '0';
            toggleBtn.style.left = (sidebarWidth - 1) + 'px';
        } else {
            sidebar.style.left = '-' + sidebarWidth + 'px';
            toggleBtn.style.left = '0';
        }
    }

    // 拖拽功能
    function startDrag(e) {
        isDragging = true;
        startY = e.clientY || e.touches[0].clientY;
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        startTop = parseInt(toggleBtn.style.top) || 50;
        
        document.addEventListener('mousemove', onDrag);
        document.addEventListener('touchmove', onDrag, { passive: false });
        document.addEventListener('mouseup', stopDrag);
        document.addEventListener('touchend', stopDrag);
        
        e.preventDefault();
    }

    function onDrag(e) {
        if (!isDragging) return;
        
        const currentY = e.clientY || e.touches[0].clientY;
        const deltaY = currentY - startY;
        const newTop = Math.max(5, Math.min(95, startTop + (deltaY / window.innerHeight) * 100));
        
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        toggleBtn.style.top = newTop + '%';
        
        e.preventDefault();
    }

    function stopDrag() {
        if (!isDragging) return;
        
        isDragging = false;
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        const currentTop = parseInt(toggleBtn.style.top);
        
        // 保存位置到localStorage
        localStorage.setItem('sidebarTogglePosition', currentTop);
        
        document.removeEventListener('mousemove', onDrag);
        document.removeEventListener('touchmove', onDrag);
        document.removeEventListener('mouseup', stopDrag);
        document.removeEventListener('touchend', stopDrag);
    }

    // 初始化时恢复位置
    function initTogglePosition() {
        const savedPosition = localStorage.getItem('sidebarTogglePosition');
        if (savedPosition) {
            const toggleBtn = document.getElementById('sidebar-toggle-btn');
            toggleBtn.style.top = savedPosition + '%';
        }
    }

    // 页面加载完成后初始化
    document.addEventListener('DOMContentLoaded', function() {
        initTogglePosition();
        
        // 绑定事件
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        toggleBtn.addEventListener('click', toggleSidebar);
        toggleBtn.addEventListener('mousedown', startDrag);
        toggleBtn.addEventListener('touchstart', startDrag, { passive: false });
        
        // 点击空白区域关闭侧边栏
        document.addEventListener('click', function(e) {
            if (sidebarVisible && !e.target.closest('#custom-sidebar') && !e.target.closest('#sidebar-toggle-btn')) {
                toggleSidebar();
            }
        });
    });

    // 页面resize时也要修正状态球位置
    window.addEventListener('resize', function() {
        const sidebar = document.getElementById('custom-sidebar');
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        const sidebarWidth = sidebar.offsetWidth;
        if (sidebarVisible) {
            toggleBtn.style.left = (sidebarWidth - 1) + 'px';
        } else {
            toggleBtn.style.left = '0';
        }
    });
})(); 
