// sidebar.min.js
// 视觉美化版：大图标、简洁悬浮按钮、高性能动画
(function() {
    // 1. 创建状态球按钮
    const toggleBtn = document.createElement('button');
    toggleBtn.id = 'sidebar-toggle-btn';
    toggleBtn.type = 'button';
    toggleBtn.title = '展开菜单';
    toggleBtn.innerHTML = `
        <span class="visually-hidden">展开侧边栏</span>
        <svg class="sidebar-toggle__icon" viewBox="0 0 24 24" role="img" aria-hidden="true">
            <path d="M9 18l6-6-6-6"></path>
        </svg>
    `;
    document.body.appendChild(toggleBtn);

    // 2. 创建侧边栏容器
    const sidebar = document.createElement('nav');
    sidebar.id = 'custom-sidebar';
    sidebar.innerHTML = '<ul id="sidebar-menu"></ul>';
    document.body.appendChild(sidebar);

    // 3. 样式注入 - 关键修改：美化形状、增大图标
    const style = document.createElement('style');
    style.textContent = `
    /* 按钮基础样式 - 更简洁的半圆形悬浮设计 */
    #sidebar-toggle-btn {
        position: fixed;
        top: 50%;
        left: 0;
        z-index: 10000;
        width: 32px;  /* 默认宽度较小，不挡视线 */
        height: 64px; /* 适中的高度 */
        padding: 0;
        border: none;
        /* 形状：左侧平，右侧圆润 */
        border-radius: 0 12px 12px 0;
        
        /* 颜色：跟随主题变量，自动适应黑白模式 */
        background: var(--card-surface, #ffffff);
        border: 1px solid var(--border-light, rgba(0,0,0,0.1));
        border-left: none;
        
        /* 阴影：柔和的悬浮感 */
        box-shadow: 2px 0 12px rgba(0,0,0,0.08);
        
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        
        /* 性能优化：GPU加速 */
        transform: translate3d(0, -50%, 0);
        will-change: transform, width; 
        transition: transform 0.3s cubic-bezier(0.2, 0, 0, 1), 
                    width 0.2s ease, 
                    background-color 0.3s, 
                    box-shadow 0.3s;
    }

    /* 鼠标悬停时稍微变宽，提示可点击 */
    #sidebar-toggle-btn:hover {
        width: 40px;
        background: var(--bg-white, #fff);
        box-shadow: 4px 0 16px rgba(0,0,0,0.12);
    }
    
    /* 图标样式 */
    #sidebar-toggle-btn .sidebar-toggle__icon {
        width: 24px;
        height: 24px;
        fill: none;
        stroke: var(--text-secondary, #666); /* 使用次级文字颜色，不刺眼 */
        stroke-width: 2;
        stroke-linecap: round;
        stroke-linejoin: round;
        transition: stroke 0.3s, transform 0.3s;
    }
    
    #sidebar-toggle-btn:hover .sidebar-toggle__icon {
        stroke: var(--primary-color, #2c3e50); /* 悬停变为主色 */
        transform: scale(1.1);
    }

    /* 侧边栏基础样式 */
    #custom-sidebar {
        position: fixed;
        top: 50%;
        left: 0; 
        z-index: 9999;
        /* 背景色跟随卡片颜色 */
        background: var(--bg-white, #fff);
        box-shadow: var(--shadow-lg, 0 8px 24px rgba(0,0,0,0.12));
        border-radius: 16px; /* 更圆润的边角 */
        padding: 20px 0;
        min-width: 80px;  /* 略微加宽 */
        max-width: 160px;
        width: auto;
        display: flex;
        flex-direction: column;
        align-items: center;
        border: 1px solid var(--border-light, rgba(0,0,0,0.05));
        
        /* 默认隐藏位置 */
        transform: translate3d(calc(-100% - 20px), -50%, 0);
        will-change: transform;
        transition: transform 0.3s cubic-bezier(0.2, 0, 0, 1);
    }

    /* 侧边栏展开状态 */
    #custom-sidebar.sidebar-open {
        transform: translate3d(20px, -50%, 0); /* 距离左边距 20px 悬浮 */
    }
    
    /* 按钮展开状态 - 箭头旋转 */
    #sidebar-toggle-btn.sidebar-open .sidebar-toggle__icon {
        transform: rotate(180deg);
    }

    /* 菜单列表 */
    #custom-sidebar ul {
        list-style: none;
        margin: 0;
        padding: 0;
        width: 100%;
        display: flex;
        flex-direction: column;
        align-items: center;
    }

    #custom-sidebar li {
        width: 100%;
        margin-bottom: 12px; /* 增加间距 */
        display: flex;
        justify-content: center;
    }
    
    #custom-sidebar li:last-child { margin-bottom: 0; }

    /* 菜单链接 - 图标放大 */
    #custom-sidebar a {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        width: calc(100% - 16px);
        min-width: 64px;
        padding: 12px 0;
        border-radius: 12px;
        color: var(--text-secondary, #666);
        text-decoration: none;
        transition: all 0.2s ease;
        position: relative;
    }

    /* 图标大小调整 - 改回原来的大小或更大 */
    #custom-sidebar a i {
        font-size: 2.25rem; /* 增大图标 */
        margin-bottom: 6px;
        transition: transform 0.2s cubic-bezier(0.34, 1.56, 0.64, 1); /* 弹性动画 */
    }
    
    #custom-sidebar a span {
        font-size: 0.9rem;
        font-weight: 500;
        opacity: 0.9;
    }

    /* 悬停效果 */
    #custom-sidebar a:hover {
        background: var(--bg-gray-light, #f5f5f5);
        color: var(--primary-color, #2c3e50);
        transform: translateY(-2px);
    }
    
    #custom-sidebar a:hover i {
        transform: scale(1.15); /* 图标放大更明显 */
        color: var(--primary-color, #2c3e50);
    }

    /* 暗色模式适配 */
    body[data-theme="dark"] #sidebar-toggle-btn {
        background: var(--surface-nav, #24292e);
        border-color: rgba(255, 255, 255, 0.08);
    }
    
    body[data-theme="dark"] #sidebar-toggle-btn .sidebar-toggle__icon {
        stroke: #a3afc3;
    }
    
    body[data-theme="dark"] #sidebar-toggle-btn:hover .sidebar-toggle__icon {
        stroke: #fff;
    }
    
    body[data-theme="dark"] #custom-sidebar {
        background: var(--surface-nav, #1e222d);
        border-color: rgba(255, 255, 255, 0.08);
    }
    
    body[data-theme="dark"] #custom-sidebar a {
        color: #8b9bb4;
    }
    
    body[data-theme="dark"] #custom-sidebar a:hover {
        background: rgba(255, 255, 255, 0.05);
        color: #fff;
    }
    
    body[data-theme="dark"] #custom-sidebar a:hover i {
        color: #fff;
    }
    `;
    document.head.appendChild(style);

    // 4. 读取数据
    fetch('data.json')
        .then(res => res.json())
        .then(data => {
            const menu = data['main-menu'];
            const ul = document.getElementById('sidebar-menu');
            if(menu) {
                Object.values(menu).forEach(item => {
                    const li = document.createElement('li');
                    li.innerHTML = `<a href="${item.href}"><i class="${item.icon}"></i><span>${item.title}</span></a>`;
                    ul.appendChild(li);
                });
            }
        });

    // 5. 逻辑控制 (保持高性能 Transform 逻辑)
    let sidebarVisible = false;
    let cachedSidebarWidth = 0;

    function updateSidebarWidth() {
        const sidebar = document.getElementById('custom-sidebar');
        cachedSidebarWidth = sidebar.offsetWidth;
    }

    function toggleSidebar() {
        sidebarVisible = !sidebarVisible;
        const sidebar = document.getElementById('custom-sidebar');
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        
        if (!cachedSidebarWidth) {
            updateSidebarWidth();
        }

        if (sidebarVisible) {
            sidebar.classList.add('sidebar-open');
            toggleBtn.classList.add('sidebar-open');
            // 计算位移：侧边栏宽度 + 侧边栏左边距(20) + 按钮与侧边栏的间隙(8)
            const moveX = cachedSidebarWidth + 20 + 8; 
            toggleBtn.style.transform = `translate3d(${moveX}px, -50%, 0)`;
        } else {
            sidebar.classList.remove('sidebar-open');
            toggleBtn.classList.remove('sidebar-open');
            toggleBtn.style.transform = `translate3d(0, -50%, 0)`;
        }
    }

    // 初始化和事件绑定
    document.addEventListener('DOMContentLoaded', function() {
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        const sidebar = document.getElementById('custom-sidebar');

        toggleBtn.addEventListener('click', (e) => {
            e.stopPropagation();
            toggleSidebar();
        });

        document.addEventListener('click', function(e) {
            if (sidebarVisible && 
                !sidebar.contains(e.target) && 
                !toggleBtn.contains(e.target)) {
                toggleSidebar();
            }
        });
        
        let resizeTimer;
        window.addEventListener('resize', () => {
            clearTimeout(resizeTimer);
            resizeTimer = setTimeout(() => {
                updateSidebarWidth();
                if (sidebarVisible) {
                    const moveX = cachedSidebarWidth + 20 + 8;
                    const toggleBtn = document.getElementById('sidebar-toggle-btn');
                    toggleBtn.style.transform = `translate3d(${moveX}px, -50%, 0)`;
                }
            }, 200);
        });
    });
})();