// sidebar.min.js
// 极简磨砂玻璃风格 - 颜色配置与顶部栏完全同步
(function() {
    // 1. 创建按钮
    const toggleBtn = document.createElement('button');
    toggleBtn.id = 'sidebar-toggle-btn';
    toggleBtn.type = 'button';
    toggleBtn.title = '展开菜单';
    toggleBtn.innerHTML = `
        <span class="visually-hidden">展开侧边栏</span>
        <svg class="sidebar-toggle__icon" viewBox="0 0 24 24" role="img" aria-hidden="true">
            <path d="M8.59 16.59L13.17 12 8.59 7.41 10 6l6 6-6 6-1.41-1.41z"></path>
        </svg>
    `;
    document.body.appendChild(toggleBtn);

    // 2. 创建侧边栏
    const sidebar = document.createElement('nav');
    sidebar.id = 'custom-sidebar';
    sidebar.innerHTML = '<ul id="sidebar-menu"></ul>';
    document.body.appendChild(sidebar);

    // 3. 样式注入
    const style = document.createElement('style');
    style.textContent = `
    /* 侧边栏按钮 - 悬浮半胶囊 */
    #sidebar-toggle-btn {
        position: fixed;
        top: 50%;
        left: 0;
        z-index: 10000;
        width: 28px;
        height: 64px;
        padding: 0;
        
        border-radius: 0 16px 16px 0; /* 更圆润 */
        
        /* 关键：使用与顶部栏相同的玻璃变量 */
        background: var(--surface-nav);
        border: 1px solid var(--border-light);
        border-left: none;
        
        /* 强力磨砂 */
        backdrop-filter: blur(20px) saturate(140%);
        -webkit-backdrop-filter: blur(20px) saturate(140%);
        
        box-shadow: var(--shadow-sm);
        
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        
        transform: translate3d(0, -50%, 0);
        will-change: transform, width; 
        transition: all 0.3s cubic-bezier(0.2, 0, 0, 1);
    }
    
    #sidebar-toggle-btn:hover {
        width: 38px;
        background: var(--surface-popover); /* 悬停变更亮/实 */
        box-shadow: var(--shadow-md);
    }

    /* 图标颜色 - 跟随主文字色 */
    #sidebar-toggle-btn .sidebar-toggle__icon {
        width: 24px;
        height: 24px;
        fill: var(--text-primary);
        transition: transform 0.3s;
        opacity: 0.8;
    }
    
    #sidebar-toggle-btn:hover .sidebar-toggle__icon {
        opacity: 1;
        transform: scale(1.1);
    }
    
    #sidebar-toggle-btn.sidebar-open .sidebar-toggle__icon {
        transform: rotate(180deg);
    }

    /* 侧边栏本体 - 纯粹玻璃板 */
    #custom-sidebar {
        position: fixed;
        top: 50%;
        left: 0;
        z-index: 9999;
        
        /* 玻璃背景 */
        background: var(--surface-nav);
        border: 1px solid var(--border-light);
        backdrop-filter: blur(20px) saturate(140%);
        
        box-shadow: var(--shadow-lg);
        border-radius: 24px; /* 大圆角 */
        padding: 20px 0;
        
        min-width: 80px;
        max-width: 180px;
        width: auto;
        
        display: flex;
        flex-direction: column;
        align-items: center;
        
        transform: translate3d(calc(-100% - 20px), -50%, 0);
        will-change: transform;
        transition: transform 0.3s cubic-bezier(0.2, 0, 0, 1);
    }

    #custom-sidebar.sidebar-open {
        transform: translate3d(20px, -50%, 0);
    }

    #custom-sidebar ul {
        list-style: none;
        margin: 0;
        padding: 0;
        width: 100%;
        display: flex;
        flex-direction: column;
        align-items: center;
    }

    #custom-sidebar li {
        width: 100%;
        margin-bottom: 8px;
        display: flex;
        justify-content: center;
    }

    /* 链接样式 */
    #custom-sidebar a {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        width: calc(100% - 16px);
        min-width: 80px; 
        padding: 12px 0;
        border-radius: 16px;
        color: var(--text-secondary);
        text-decoration: none;
        transition: all 0.2s ease;
    }

    #custom-sidebar a i {
        font-size: 2.0rem; 
        margin-bottom: 4px;
        transition: transform 0.2s;
        color: var(--text-primary);
    }
    
    #custom-sidebar a span {
        font-size: 1.4rem; 
        font-weight: 500;
        margin-top: 2px;
    }

    /* 悬停 - 极简高亮 */
    #custom-sidebar a:hover {
        background: var(--surface-input);
        color: var(--text-primary);
        transform: translateY(-2px);
    }
    
    /* 夜间模式适配：由于使用了 CSS 变量，无需额外写大量代码，
       它会自动继承 app.css 中的 --surface-nav (黑玻璃) 和 --text-primary (白字) */
    `;
    document.head.appendChild(style);

    // 4. 数据读取与逻辑 (保持不变)
    fetch('data.json')
        .then(res => res.json())
        .then(data => {
            const menu = data['main-menu'];
            const ul = document.getElementById('sidebar-menu');
            if(menu) {
                Object.values(menu).forEach(item => {
                    const li = document.createElement('li');
                    li.innerHTML = `<a href="${item.href}"><i class="${item.icon}"></i><span>${item.title}</span></a>`;
                    ul.appendChild(li);
                });
            }
        });

    let sidebarVisible = false;
    let cachedSidebarWidth = 0;

    function updateSidebarWidth() {
        const sidebar = document.getElementById('custom-sidebar');
        cachedSidebarWidth = sidebar.offsetWidth;
    }

    function toggleSidebar() {
        sidebarVisible = !sidebarVisible;
        const sidebar = document.getElementById('custom-sidebar');
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        
        if (!cachedSidebarWidth) updateSidebarWidth();

        if (sidebarVisible) {
            sidebar.classList.add('sidebar-open');
            toggleBtn.classList.add('sidebar-open');
            const moveX = cachedSidebarWidth + 20 + 8; 
            toggleBtn.style.transform = `translate3d(${moveX}px, -50%, 0)`;
        } else {
            sidebar.classList.remove('sidebar-open');
            toggleBtn.classList.remove('sidebar-open');
            toggleBtn.style.transform = `translate3d(0, -50%, 0)`;
        }
    }

    document.addEventListener('DOMContentLoaded', function() {
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        const sidebar = document.getElementById('custom-sidebar');

        toggleBtn.addEventListener('click', (e) => {
            e.stopPropagation();
            toggleSidebar();
        });

        document.addEventListener('click', function(e) {
            if (sidebarVisible && 
                !sidebar.contains(e.target) && 
                !toggleBtn.contains(e.target)) {
                toggleSidebar();
            }
        });
        
        let resizeTimer;
        window.addEventListener('resize', () => {
            clearTimeout(resizeTimer);
            resizeTimer = setTimeout(() => {
                updateSidebarWidth();
                if (sidebarVisible) {
                    const toggleBtn = document.getElementById('sidebar-toggle-btn');
                    const moveX = cachedSidebarWidth + 20 + 8;
                    toggleBtn.style.transform = `translate3d(${moveX}px, -50%, 0)`;
                }
            }, 200);
        });
    });
})();