// sidebar.min.js
// 侧边栏逻辑：恢复经典大字体/大图标，保留高性能动画，移除背景设置干扰
(function() {
    // 1. 创建状态球按钮
    const toggleBtn = document.createElement('button');
    toggleBtn.id = 'sidebar-toggle-btn';
    toggleBtn.type = 'button';
    toggleBtn.title = '展开菜单';
    toggleBtn.innerHTML = `
        <span class="visually-hidden">展开侧边栏</span>
        <svg class="sidebar-toggle__icon" viewBox="0 0 24 24" role="img" aria-hidden="true">
            <path d="M6 7h12v2H6V7zm0 4h12v2H6v-2zm0 4h12v2H6v-2z"></path>
        </svg>
    `;
    document.body.appendChild(toggleBtn);

    // 2. 创建侧边栏容器
    const sidebar = document.createElement('nav');
    sidebar.id = 'custom-sidebar';
    sidebar.innerHTML = '<ul id="sidebar-menu"></ul>';
    document.body.appendChild(sidebar);

    // 3. 样式注入
    const style = document.createElement('style');
    style.textContent = `
    /* 按钮样式 */
    #sidebar-toggle-btn {
        position: fixed;
        top: 50%;
        left: 0;
        z-index: 10000;
        width: 40px;
        height: 92px;
        padding: 0;
        border: none;
        border-radius: 0 38px 38px 0;
        background: linear-gradient(180deg, #3b4e60, #1c2732);
        box-shadow: 2px 0 8px rgba(0,0,0,0.2);
        display: flex;
        align-items: center;
        justify-content: flex-end;
        cursor: pointer;
        transform: translate3d(0, -50%, 0);
        will-change: transform; 
        transition: transform 0.3s cubic-bezier(0.2, 0, 0, 1), box-shadow 0.3s ease;
    }
    
    #sidebar-toggle-btn::after {
        content: "";
        position: absolute;
        inset: 3px 6px 3px 0;
        border-radius: 0 32px 32px 0;
        border: 1.5px solid rgba(255, 255, 255, 0.12);
        border-left: none;
        pointer-events: none;
    }

    #sidebar-toggle-btn .sidebar-toggle__icon {
        width: 24px;
        height: 24px;
        margin-right: 8px;
        fill: #e6eaef;
        transition: fill 0.3s ease;
    }
    
    #sidebar-toggle-btn:hover .sidebar-toggle__icon {
        fill: #ffffff;
    }

    /* 侧边栏基础样式 */
    #custom-sidebar {
        position: fixed;
        top: 50%;
        left: 0;
        z-index: 9999;
        background: var(--bg-white, rgba(255, 255, 255, 0.95));
        box-shadow: var(--shadow-md);
        border-radius: 22px;
        padding: 18px 0;
        min-width: 72px;
        max-width: 180px; /* 增加最大宽度以适应大字体 */
        width: auto;
        display: flex;
        flex-direction: column;
        align-items: center;
        border: 1px solid var(--border-light);
        
        /* 默认隐藏 */
        transform: translate3d(calc(-100% - 20px), -50%, 0);
        will-change: transform;
        transition: transform 0.3s cubic-bezier(0.2, 0, 0, 1);
    }

    /* 展开状态 */
    #custom-sidebar.sidebar-open {
        transform: translate3d(20px, -50%, 0);
    }

    /* 菜单列表 */
    #custom-sidebar ul {
        list-style: none;
        margin: 0;
        padding: 0;
        width: 100%;
        display: flex;
        flex-direction: column;
        align-items: center;
    }

    #custom-sidebar li {
        width: 100%;
        margin-bottom: 8px;
        display: flex;
        justify-content: center;
    }

    /* 菜单链接 - 恢复原来的大尺寸 */
    #custom-sidebar a {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        width: calc(100% - 12px);
        min-width: 80px; /* 保证宽度 */
        padding: 12px 0;
        border-radius: 16px;
        color: var(--text-primary);
        text-decoration: none;
        transition: background 0.2s, color 0.2s, transform 0.1s;
    }

    /* 图标大小恢复 */
    #custom-sidebar a i {
        font-size: 2.1rem; /* 恢复原大小 */
        margin-bottom: 4px;
        transition: transform 0.2s;
    }
    
    /* 文字大小恢复 */
    #custom-sidebar a span {
        font-size: 1.5rem; /* 恢复原大小 */
        font-weight: 500;
        margin-top: 2px;
    }

    #custom-sidebar a:hover {
        background: var(--primary-gradient);
        color: #fff;
        transform: scale(1.05);
        box-shadow: 0 4px 10px rgba(0,0,0,0.1);
    }
    
    #custom-sidebar a:hover i,
    #custom-sidebar a:hover span {
        color: #fff;
    }

    /* 暗色模式适配 */
    body[data-theme="dark"] #custom-sidebar {
        background: rgba(30, 34, 40, 0.95);
        border-color: rgba(255, 255, 255, 0.08);
    }
    
    body[data-theme="dark"] #custom-sidebar a {
        color: #a3afc3;
    }
    
    body[data-theme="dark"] #custom-sidebar a:hover {
        color: #fff;
    }
    `;
    document.head.appendChild(style);

    // 4. 读取数据
    fetch('data.json')
        .then(res => res.json())
        .then(data => {
            const menu = data['main-menu'];
            const ul = document.getElementById('sidebar-menu');
            if(menu) {
                Object.values(menu).forEach(item => {
                    const li = document.createElement('li');
                    li.innerHTML = `<a href="${item.href}"><i class="${item.icon}"></i><span>${item.title}</span></a>`;
                    ul.appendChild(li);
                });
            }
        });

    // 5. 逻辑控制
    let sidebarVisible = false;
    let cachedSidebarWidth = 0;

    function updateSidebarWidth() {
        const sidebar = document.getElementById('custom-sidebar');
        cachedSidebarWidth = sidebar.offsetWidth;
    }

    function toggleSidebar() {
        sidebarVisible = !sidebarVisible;
        const sidebar = document.getElementById('custom-sidebar');
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        
        if (!cachedSidebarWidth) {
            updateSidebarWidth();
        }

        if (sidebarVisible) {
            sidebar.classList.add('sidebar-open');
            toggleBtn.classList.add('sidebar-open');
            // 计算位移：侧边栏宽度 + 左边距(20) + 贴合修正(-1)
            const moveX = cachedSidebarWidth + 20 - 1; 
            toggleBtn.style.transform = `translate3d(${moveX}px, -50%, 0)`;
        } else {
            sidebar.classList.remove('sidebar-open');
            toggleBtn.classList.remove('sidebar-open');
            toggleBtn.style.transform = `translate3d(0, -50%, 0)`;
        }
    }

    document.addEventListener('DOMContentLoaded', function() {
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        const sidebar = document.getElementById('custom-sidebar');

        toggleBtn.addEventListener('click', (e) => {
            e.stopPropagation();
            toggleSidebar();
        });

        document.addEventListener('click', function(e) {
            if (sidebarVisible && 
                !sidebar.contains(e.target) && 
                !toggleBtn.contains(e.target)) {
                toggleSidebar();
            }
        });
        
        let resizeTimer;
        window.addEventListener('resize', () => {
            clearTimeout(resizeTimer);
            resizeTimer = setTimeout(() => {
                updateSidebarWidth();
                if (sidebarVisible) {
                    const toggleBtn = document.getElementById('sidebar-toggle-btn');
                    const moveX = cachedSidebarWidth + 20 - 1;
                    toggleBtn.style.transform = `translate3d(${moveX}px, -50%, 0)`;
                }
            }, 200);
        });
    });
})();