// sidebar.min.js
// 动态生成悬浮左侧边栏，内容从 data.json main-menu 读取，默认隐藏，仅显示状态球，点击展开/收起

(function() {
    // 创建状态球按钮
    const toggleBtn = document.createElement('div');
    toggleBtn.id = 'sidebar-toggle-btn';
    toggleBtn.title = '展开菜单';
    toggleBtn.innerHTML = '<span></span>';
    document.body.appendChild(toggleBtn);

    // 创建侧边栏容器
    const sidebar = document.createElement('nav');
    sidebar.id = 'custom-sidebar';
    sidebar.innerHTML = '<ul id="sidebar-menu"></ul>';
    document.body.appendChild(sidebar);

    // 样式注入
    const style = document.createElement('style');
    style.textContent = `
    #sidebar-toggle-btn {
        position: fixed;
        top: 50%;
        left: 0;
        z-index: 10000;
        width: 50px;
        height: 50px;
        background: linear-gradient(145deg, #ffffff, #f0f0f0);
        border-radius: 50%;
        box-shadow:
            6px 6px 12px rgba(0, 0, 0, 0.2),
            -4px -4px 8px rgba(255, 255, 255, 1),
            inset 0 1px 2px rgba(255, 255, 255, 1),
            inset 0 -1px 2px rgba(0, 0, 0, 0.1);
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        transform: translateY(-50%);
        transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        border: 2px solid rgba(0, 0, 0, 0.1);
        overflow: hidden;
    }
    /* 金属光泽效果 */
    #sidebar-toggle-btn::before {
        content: '';
        position: absolute;
        top: -50%;
        left: -50%;
        width: 200%;
        height: 200%;
        background: linear-gradient(
            45deg,
            transparent 30%,
            rgba(255, 255, 255, 0.4) 50%,
            transparent 70%
        );
        transform: translateX(-100%) translateY(-100%) rotate(45deg);
        transition: transform 0.6s;
    }
    #sidebar-toggle-btn span {
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 20px;
        color: #1a1a1a;
        z-index: 1;
        text-shadow: 0 1px 2px rgba(255, 255, 255, 1);
        transition: transform 0.3s ease;
    }
    #sidebar-toggle-btn:hover {
        transform: translateY(-50%) translateY(-4px);
        box-shadow:
            8px 8px 16px rgba(0, 0, 0, 0.25),
            -6px -6px 12px rgba(255, 255, 255, 1),
            inset 0 1px 3px rgba(255, 255, 255, 1),
            inset 0 -1px 3px rgba(0, 0, 0, 0.15);
        background: linear-gradient(145deg, #ffffff, #f8f8f8);
        border-color: rgba(0, 0, 0, 0.15);
    }
    #sidebar-toggle-btn:hover::before {
        transform: translateX(100%) translateY(100%) rotate(45deg);
    }
    #sidebar-toggle-btn:hover span {
        transform: scale(1.2) rotate(180deg);
    }
    #sidebar-toggle-btn:active {
        transform: translateY(-50%) translateY(-2px);
        box-shadow:
            inset 3px 3px 6px rgba(0, 0, 0, 0.2),
            inset -3px -3px 6px rgba(255, 255, 255, 0.9);
    }
    #custom-sidebar {
        position: fixed;
        top: 50%;
        left: -220px;
        z-index: 9999;
        background: linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.9) 100%);
        box-shadow: 0 8px 32px rgba(102,126,234,0.10);
        border-radius: 22px;
        padding: 18px 0 18px 0;
        min-width: 72px;
        max-width: 160px;
        width: auto;
        display: flex;
        flex-direction: column;
        align-items: center;
        transition: left 0.35s cubic-bezier(.77,0,.18,1), box-shadow 0.3s;
        transform: translateY(-50%);
    }
    #custom-sidebar.sidebar-open {
        left: 48px;
        box-shadow: 0 8px 32px rgba(102,126,234,0.18);
    }
    #custom-sidebar ul {
        list-style: none;
        margin: 0;
        padding: 0;
        width: 100%;
        display: flex;
        flex-direction: column;
        align-items: center;
    }
    #custom-sidebar li {
        width: 100%;
        margin: 0 0 8px 0;
        display: flex;
        flex-direction: column;
        align-items: center;
    }
    #custom-sidebar li:last-child { margin-bottom: 0; }
    #custom-sidebar a {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        width: calc(100% - 12px);
        min-width: 72px;
        max-width: 160px;
        padding: 12px 0 8px 0;
        margin: 0 6px 0 6px;
        border-radius: 16px;
        color: #4a5568;
        font-size: 1.15rem;
        font-weight: 500;
        background: none;
        border: none;
        transition: background 0.3s, color 0.3s, box-shadow 0.3s;
        cursor: pointer;
        text-decoration: none;
        position: relative;
        gap: 4px;
        box-sizing: border-box;
    }
    #custom-sidebar a i {
        font-size: 2.1rem;
        margin-bottom: 2px;
        transition: color 0.3s, transform 0.3s;
    }
    #custom-sidebar a:hover {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        color: #fff;
        box-shadow: 0 8px 25px rgba(102, 126, 234, 0.18);
    }
    #custom-sidebar a:hover i {
        color: #fff;
        transform: scale(1.12);
    }
    #custom-sidebar a:active {
        transform: scale(0.97);
    }
    #custom-sidebar a span {
        margin-top: 2px;
        font-size: 1.50rem;
        font-weight: 400;
        letter-spacing: 0.5px;
        transition: color 0.3s;
    }
    @media (max-width: 700px) {
        #sidebar-toggle-btn {
            left: 0;
            width: 32px;
            height: 32px;
        }
        #custom-sidebar {
            left: -140px;
            min-width: 56px;
            max-width: 100px;
        }
        #custom-sidebar.sidebar-open {
            left: 36px;
        }
        #custom-sidebar a span {
            font-size: 1.1rem;
        }
        #custom-sidebar a i {
            font-size: 1.2rem;
        }
    }
    body.dark-theme #custom-sidebar {
        background: linear-gradient(135deg, rgba(36,41,46,0.96) 0%, rgba(36,41,46,0.92) 100%);
        box-shadow: 0 8px 32px rgba(40,48,80,0.18);
        border-radius: 22px;
    }
    body.dark-theme #custom-sidebar a {
        color: #e2e8f0;
        background: none;
    }
    body.dark-theme #custom-sidebar a:hover {
        background: linear-gradient(135deg, #3b3f5c 0%, #5a5e7a 100%);
        color: #fff;
        box-shadow: 0 8px 25px rgba(80, 120, 255, 0.13);
    }
    body.dark-theme #custom-sidebar a:hover i {
        color: #fff;
    }
    body.dark-theme #custom-sidebar a:active {
        background: #23263a;
    }
    body.dark-theme #custom-sidebar a span {
        color: #e2e8f0;
    }
    body.dark-theme #sidebar-toggle-btn {
        background: linear-gradient(135deg, #23263a 0%, #3b3f5c 100%);
        border-right: 2.5px solid #23263a;
        box-shadow: 0 2px 12px rgba(40,48,80,0.18), 0 0 0 2px #23263a inset;
    }
    body.dark-theme #sidebar-toggle-btn span {
        background: radial-gradient(circle at 60% 40%, #fff 70%, #b3b8d4 100%);
        box-shadow: 0 0 8px #23263a88, 0 0 0 2px #23263a66;
    }
    body.dark-theme #sidebar-toggle-btn:hover {
        background: linear-gradient(135deg, #35395a 0%, #5a5e7a 100%);
        border-right: 3px solid #35395a;
        box-shadow: 0 8px 32px rgba(80,120,255,0.18), 0 0 0 3px #35395a inset;
    }
    body.dark-theme #sidebar-toggle-btn:hover span {
        box-shadow: 0 0 16px #fff, 0 0 0 3px #35395a;
    }
    /* 选中按钮高亮与header按钮一致 */
    #custom-sidebar a.active,
    #custom-sidebar a:focus {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%) !important;
        color: #fff !important;
        box-shadow: 0 8px 25px rgba(102, 126, 234, 0.18);
    }
    #custom-sidebar a.active i,
    #custom-sidebar a:focus i {
        color: #fff !important;
    }
    /* 夜间模式选中按钮高亮 */
    body.dark-theme #custom-sidebar a.active,
    body.dark-theme #custom-sidebar a:focus {
        background: linear-gradient(135deg, #3b3f5c 0%, #5a5e7a 100%) !important;
        color: #fff !important;
        box-shadow: 0 8px 25px rgba(80, 120, 255, 0.13);
    }
    body.dark-theme #custom-sidebar a.active i,
    body.dark-theme #custom-sidebar a:focus i {
        color: #fff !important;
    }
    `;
    document.head.appendChild(style);

    // 读取 data.json main-menu
    fetch('data.json')
        .then(res => res.json())
        .then(data => {
            const menu = data['main-menu'];
            const ul = document.getElementById('sidebar-menu');
            Object.values(menu).forEach(item => {
                const li = document.createElement('li');
                li.innerHTML = `<a href="${item.href}"><i class="${item.icon}"></i><span>${item.title}</span></a>`;
                ul.appendChild(li);
            });
        });

    // 侧边栏切换功能
    let sidebarVisible = false;
    let isDragging = false;
    let startY = 0;
    let startTop = 0;

    function toggleSidebar() {
        if (isDragging) return; // 如果正在拖拽，不切换侧边栏
        
        sidebarVisible = !sidebarVisible;
        const sidebar = document.getElementById('custom-sidebar');
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        const sidebarWidth = sidebar.offsetWidth;
        
        if (sidebarVisible) {
            sidebar.style.left = '0';
            // 让状态球紧贴侧边栏右侧
            toggleBtn.style.left = (sidebarWidth - 1) + 'px';
        } else {
            sidebar.style.left = '-' + sidebarWidth + 'px';
            toggleBtn.style.left = '0';
        }
    }

    // 拖拽功能
    function startDrag(e) {
        isDragging = true;
        startY = e.clientY || e.touches[0].clientY;
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        startTop = parseInt(toggleBtn.style.top) || 50;
        
        document.addEventListener('mousemove', onDrag);
        document.addEventListener('touchmove', onDrag, { passive: false });
        document.addEventListener('mouseup', stopDrag);
        document.addEventListener('touchend', stopDrag);
        
        e.preventDefault();
    }

    function onDrag(e) {
        if (!isDragging) return;
        
        const currentY = e.clientY || e.touches[0].clientY;
        const deltaY = currentY - startY;
        const newTop = Math.max(5, Math.min(95, startTop + (deltaY / window.innerHeight) * 100));
        
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        toggleBtn.style.top = newTop + '%';
        
        e.preventDefault();
    }

    function stopDrag() {
        if (!isDragging) return;
        
        isDragging = false;
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        const currentTop = parseInt(toggleBtn.style.top);
        
        // 保存位置到localStorage
        localStorage.setItem('sidebarTogglePosition', currentTop);
        
        document.removeEventListener('mousemove', onDrag);
        document.removeEventListener('touchmove', onDrag);
        document.removeEventListener('mouseup', stopDrag);
        document.removeEventListener('touchend', stopDrag);
    }

    // 初始化时恢复位置
    function initTogglePosition() {
        const savedPosition = localStorage.getItem('sidebarTogglePosition');
        if (savedPosition) {
            const toggleBtn = document.getElementById('sidebar-toggle-btn');
            toggleBtn.style.top = savedPosition + '%';
        }
    }

    // 页面加载完成后初始化
    document.addEventListener('DOMContentLoaded', function() {
        initTogglePosition();
        
        // 绑定事件
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        toggleBtn.addEventListener('click', toggleSidebar);
        toggleBtn.addEventListener('mousedown', startDrag);
        toggleBtn.addEventListener('touchstart', startDrag, { passive: false });
        
        // 点击空白区域关闭侧边栏
        document.addEventListener('click', function(e) {
            if (sidebarVisible && !e.target.closest('#custom-sidebar') && !e.target.closest('#sidebar-toggle-btn')) {
                toggleSidebar();
            }
        });
    });

    // 页面resize时也要修正状态球位置
    window.addEventListener('resize', function() {
        const sidebar = document.getElementById('custom-sidebar');
        const toggleBtn = document.getElementById('sidebar-toggle-btn');
        const sidebarWidth = sidebar.offsetWidth;
        if (sidebarVisible) {
            toggleBtn.style.left = (sidebarWidth - 1) + 'px';
        }
    });
})(); 